document.addEventListener("DOMContentLoaded",function(){if(document.getElementById("countryChart")){var ctx=document.getElementById("countryChart").getContext("2d");new Chart(ctx,{type:"bar",data:{labels:countryData.labels,datasets:[{label:"Visits by Country",data:countryData.data,backgroundColor:getColors(countryData.data.length)}]}})}if(document.getElementById("browserChart")){var ctx=document.getElementById("browserChart").getContext("2d");new Chart(ctx,{type:"pie",data:{labels:browserData.labels,datasets:[{label:"Browser Usage",data:browserData.data,backgroundColor:getColors(browserData.data.length)}]}})}if(document.getElementById("visitsSummaryChart")){var ctx=document.getElementById("visitsSummaryChart").getContext("2d");new Chart(ctx,{type:"line",data:{labels:visitsSummaryData.labels,datasets:[{label:"Visits Summary",data:visitsSummaryData.data,backgroundColor:getColors(visitsSummaryData.data.length)}]}})}});function hexToRGBA(hex,alpha=.2){hex=hex.replace(/^#/,"");var r=parseInt(hex.substring(0,2),16);var g=parseInt(hex.substring(2,4),16);var b=parseInt(hex.substring(4,6),16);return`rgba(${r}, ${g}, ${b}, ${alpha})`}function getColors(length){var hexColors=["#ec4899","#10b981","#8b5cf6","#06b6d4","#22c55e","#f97316","#a855f7","#3b82f6","#ef4444","#f43f5e","#eab308","#84cc16","#f59e0b","#d946ef","#6366f1","#78716c","#14b8a6","#0ea5e9"];var rgbaColors=hexColors.map(hex=>hexToRGBA(hex));return rgbaColors.slice(0,length)}